	var fieldsToValidate = formWrapper.querySelectorAll('input[type="text"][required], input[type="radio"][required], input[type="checkbox"][required], input[type="email"][required], input[type="tel"][required], textarea[required]');
	var inputValidity;
	var elemErrorAttr;
	var elemDescribedby;
	var backdrop = document.querySelector('.myc-loading');
	var backdropTitle = backdrop.querySelector('h1');
	var backdropTitleMsg = backdrop.querySelector('h1').dataset.msg;
	var errorBanner = document.querySelector('#gralError');
	var happyPath = document.querySelector('#happypath');
	var happyPathText = happyPath.querySelectorAll('.myc-option-chosen');
	var sadPath = document.querySelector('#sadpath');
	var formObj = {};
	var http;
	var url;
	var params;
	var nameParam;
	var urlEncodedDataPairs = [];
	var goBackBtn = document.querySelectorAll('.myc-wordBtn');

	function slideStep(elem, isTransitioned, secondElem) {
		if (isTransitioned) {
			elem.setAttribute('hidden', true);
			secondElem.removeAttribute('hidden');
		} else {
			elem.removeAttribute('hidden');
			secondElem.setAttribute('hidden', true);
			elem.classList.remove('resizeWidth');
			//to do after transition ends, add hidden 
			elem.focus();
			Array.prototype.forEach.call(document.querySelector('.myc-subscription-form-ending'), function(elem) {
				elem.setAttribute('hidden', true);
			});
		}
	}

	function processTransitionEnd(e, result) {
		slideStep(e, true, document.querySelector('#second-step'));
		if (result) {
			happyPath.removeAttribute('hidden');
			happyPath.focus();
			Array.prototype.forEach.call(document.querySelectorAll('input:checked + label'), function(inputElem) {
				Array.prototype.forEach.call(happyPathText, function(choosenOption) {
					choosenOption.textContent = inputElem.textContent;
				});
			});
		} else {
			sadPath.removeAttribute('hidden');
			sadPath.focus();
		}
		loadBack('', false);
	}

	function finalStep(result) {
		var e = document.querySelector('#first-step');
		e.classList.add('resizeWidth');

		processTransitionEnd(e, result);
	}

	function sendingData(datatoSend) {
		http = new XMLHttpRequest();
		url = 'https://s74881809.t.eloqua.com/e/f2';
		params = datatoSend;
		http.open('POST', url, true);
		for (nameParam in params) {
			urlEncodedDataPairs.push(encodeURIComponent(nameParam) + '=' + encodeURIComponent(params[nameParam]));
		}
		//Send the proper header information along with the request
		http.setRequestHeader('Content-type', 'application/x-www-form-urlencoded');


		http.onreadystatechange = function () { //Call a function when the state changes.		
			if (http.readyState === 4) {
				switch (true) {
					case (http.status == 200):
						//success
						finalStep(true);
						break;
					case (http.status >= 500):
						// internal server error
						finalStep(false);
						break;
					case (http.status >= 402 && http.status <= 420):
						// error
						finalStep(false);
						break;
					case (http.status == 400 || http.status == 401):
						// bad request & unauthorized error
						finalStep(false);
						break;
					default:
						console.log('issue with http status');
				}
			}
		}
		http.send(urlEncodedDataPairs.join("&"));
	}

	//Get all the information from the form
	function createObj() {
		Array.prototype.forEach.call(document.querySelectorAll('.myc-subscription-form input'), function(inputElem) {
			var addVal = true;
			if (inputElem.type == "radio" && !inputElem.checked) {
				addVal = false;
			}
			if (addVal) {
				formObj[inputElem.getAttribute('name')] = inputElem.value;
			}
		});
		sendingData(formObj);
		//finalStep(true);
	}

	//Clean Error Validation
	function cleanValidation(el) {
		if (el.getAttribute('type') === 'radio' || el.getAttribute('type') === 'checkboxes') {
			el.parentNode.classList.remove('myc-error');
		} else {
			el.parentNode.parentNode.classList.remove('myc-error');
		}
		if (el.hasAttribute('aria-describedby')) {
			elemDescribedby = el.getAttribute('aria-describedby');
			document.querySelector('#' + elemDescribedby).setAttribute('hidden', true);
			el.removeAttribute('aria-describedby');
		}
	}

	//Error handling
	function errorHandling(el, errorType) {
		if (el.getAttribute('type') === 'radio' || el.getAttribute('type') === 'checkboxes') {
			el.parentNode.classList.add('myc-error');
		} else {
			el.parentNode.parentNode.classList.add('myc-error');
		}
		if (el.hasAttribute('data-' + errorType)) {
			elemErrorAttr = el.getAttribute('data-' + errorType);
			el.setAttribute('aria-describedby', elemErrorAttr);
			document.querySelector('#' + elemErrorAttr).removeAttribute('hidden');
		}
	}

	//Validate input
	function validInput(field) {
		cleanValidation(field);
		inputValidity = field.validity;
		if (!inputValidity.valid) {
			for (var prop in inputValidity) {
				if (inputValidity[prop] === true) {
					//Error Handling
					errorHandling(field, prop)
					return false;
				}
			}
		}
	}

	function validateForm(ftv) {
		Array.prototype.forEach.call(ftv, function(field) {
			validInput(field);
		});
		if (document.querySelectorAll('.myc-error').length > 0) {
			return true;
		} else {
			return false;
		}
	}

	//Function for showing backdrop
	//showBackDrop is a boolean to show or hide
	function loadBack(msg, showBackDrop) {
		backdropTitle.textContent = msg;
		if (showBackDrop) {
			backdrop.removeAttribute('hidden');
			backdrop.focus();
		} else {
			backdrop.blur();
			backdrop.setAttribute('hidden', true);
		}
	}

	function addingInlineEvents() {
		Array.prototype.forEach.call(fieldsToValidate, function(field) {
			if (field.getAttribute('type') === 'radio' || field.getAttribute('type') === 'checkbok') {
				field.addEventListener('change', function() {
					validInput(this);
				});
			} else {
				field.addEventListener('input', function() {
					validInput(this);
				});
			}
		});
	}

	//Function for initialize the main logic
	function init() {
		//Validation on submit button click
		document.querySelector('.myc-primaryBtn').addEventListener('click', function(e) {
			e.preventDefault();
			addingInlineEvents();
			if (validateForm(fieldsToValidate)) {
				errorBanner.removeAttribute('hidden');
				errorBanner.focus();
			} else {
				errorBanner.setAttribute('hidden', true);
				loadBack(backdropTitleMsg, true);
				createObj();
			}
		}, false);

		//Go Back To first step
		Array.prototype.forEach.call(goBackBtn, function (btn) {
			btn.addEventListener('click', function (e) {
				e.preventDefault();
				slideStep(document.querySelector('#first-step'), false, document.querySelector('#second-step'));
			});
		});

		//if the page is created using .Net form
		if(document.querySelector('#mayoform') !== null) {
			//
			document.querySelector('#Search').addEventListener('click', function(e){
				e.preventDefault();
				if(document.querySelector('#searchTerm').value !== "") {
					window.location = 'https://www.mayoclinic.org/search/search-results?q='+document.querySelector('#searchTerm').value;
				}
			}, false);
		}		
	}

	//start main logic
	document.addEventListener('DOMContentLoaded', init);		
})();










            Request an Appointment at Mayo Clinic

Symptoms:
With a seizure, signs and symptoms can range from mild to severe and vary depending on the type of seizure. Seizure signs and symptoms may include:

    Temporary confusion
    A staring spell
    Uncontrollable jerking movements of the arms and legs
    Loss of consciousness or awareness
    Cognitive or emotional symptoms, such as fear, anxiety or deja vu

Doctors generally classify seizures as either focal or generalized, based on how and where abnormal brain activity begins. Seizures may also be classified as unknown onset, if how the seizure began isn't known.
        
            
            Focal seizuresFocal seizures result from abnormal electrical activity in one area of your brain. Focal seizures can occur with or without loss of consciousness:

    Focal seizures with impaired awareness. These seizures involve a change or loss of consciousness or awareness that feels like being in a dream. You may seem awake, but you stare into space and do not respond normally to your environment or you perform repetitive movements. These may include hand rubbing, mouth movements, repeating certain words or walking in circles. You may not remember the seizure or even know that it occurred.
    Focal seizures without loss of consciousness. These seizures may alter emotions or change the way things look, smell, feel, taste or sound, but you don't lose consciousness. You may suddenly feel angry, joyful or sad. Some people have nausea or unusual feelings that are difficult to describe. These seizures may also result in difficulty speaking, involuntary jerking of a body part, such as an arm or a leg, and spontaneous sensory symptoms such as tingling, dizziness and seeing flashing lights.

Symptoms of focal seizures may be confused with other neurological disorders, such as migraine, narcolepsy or mental illness.
        
            
            Generalized seizuresSeizures that appear to involve all areas of the brain are called generalized seizures. Different types of generalized seizures include:

    Absence seizures. Absence seizures, previously known as petit mal seizures, often occur in children and are characterized by staring into space or by subtle body movements, such as eye blinking or lip smacking. They usually last for five to 10 seconds but may happen up to hundreds of times per day. These seizures may occur in clusters and cause a brief loss of awareness.
    Tonic seizures. Tonic seizures cause stiffening of your muscles. These seizures usually affect muscles in your back, arms and legs and may cause you to lose consciousness and fall to the ground.
    Atonic seizures. Atonic seizures, also known as drop seizures, cause a loss of muscle control, which may cause you to suddenly collapse, fall down or drop your head.
    Clonic seizures. Clonic seizures are associated with repeated or rhythmic, jerking muscle movements. These seizures usually affect the neck, face and arms on both sides of the body.
    Myoclonic seizures. Myoclonic seizures usually appear as sudden brief jerks or twitches of your arms and legs. There is often no loss of consciousness.
    Tonic-clonic seizures. Tonic-clonic seizures, previously known as grand mal seizures, are the most dramatic type of epileptic seizure and can cause an abrupt loss of consciousness, body stiffening and shaking, and sometimes loss of bladder control or biting your tongue. They may last for several minutes.

        
            
            When to see a doctorSeek immediate medical help if any of the following occurs:

    The seizure lasts more than five minutes.
    Breathing or consciousness doesn't return after the seizure stops.
    A second seizure follows immediately.
    You have a high fever.
    You're experiencing heat exhaustion.
    You're pregnant.
    You have diabetes.
    You've injured yourself during the seizure.

If you experience a seizure for the first time, seek medical advice.







